mapper.select:
select t.article_no, t.user_id, t.profile, t.content, count(recommend_id) as recommend_cnt, t.createdAt from ( select b.article_no, b.user_id, u.profile, b.content, b.createdAt from board b join users u on b.user_id = u.user_id ) as t left join recommend as r on t.article_no = r.article_no group by t.article_no order by
recommend_cnt desc,
t.article_no desc limit #{start}, #{listsize}

select d.article_no, d.user_id, d.profile, d.content, count(recommend_id) as recommend_cnt, d.createdAt from ( select c.article_no, c.user_id, c.content, c.createdAt, u.profile from ( select a.article_no, a.user_id, a.content, a.createdAt from board a join ( select distinct(article_no) from hashtag h join board_tag b on h.hashtag_id = b.hashtag_id where h.name = #{hashtag} ) b on a.article_no = b.article_no ) c join users u on c.user_id = u.user_id ) d left join recommend as r on d.article_no = r.article_no group by d.article_no order by
recommend_cnt desc,
d.article_no desc limit #{start}, #{listsize}

select save_folder, save_file, original_name
from board_images
where article_no = #{articleNo}

select t.article_no, t.user_id, t.profile, t.content, count(recommend_id) as recommend_cnt, t.createdAt
from (
	select b.article_no, b.user_id, u.profile, b.content, b.createdAt
	from board b
	join users u
	on b.user_id = u.user_id
) as t
left join recommend as r
on t.article_no = r.article_no
where t.article_no = #{articleNo}

select count(*)
from recommend
where article_no = #{articleNo}

select count(*)
from recommend
where article_no = #{articleNo} and user_id = #{userId}

mapper.update:
update board
set content = #{content}
where article_no = #{articleNo}

mapper.select:
select hashtag_id
from hashtag
where name = #{tag}
