mapper.update:
UPDATE users
SET user_password = #{userPassword}
WHERE user_id = #{userId};

mapper.select:
SELECT user_id, user_name, email, join_date, profile
FROM users
WHERE user_id = #{userId};

SELECT user_id
FROM users
WHERE user_id = #{userId};

SELECT user_id, user_name, email, join_date, profile
FROM users
WHERE user_id= #{userId} AND user_password= #{userPassword};

SELECT user_id
FROM users
WHERE email = #{email};

mapper.update:
UPDATE users
SET user_password = #{userPassword}
WHERE user_id = #{userId}

UPDATE users
SET profile = #{profile}
WHERE user_id = #{userId}
